package cmm02.op;

public class No04_ConditionDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int val01=1;
		int val02=2;
		
		if((val01 == 1) && (val02 == 2)){
			System.out.println("ㅇㅇ 1 2번 맞음");
		}
		if((val01 != 1) || (val02 != 2)){
			System.out.println("1 2 번 다름");
		}
		
		
	}

}
